script:
{
    props: ['list', 'name'],
    data: {
        myList: list,
        myName: name
    },
    components: {
        'todo-list': './List.atom',
        'app-layout': '../common/component/Layout.atom'
    }
}

module.exports = {
    methods: {
        addLike(itemName) {
            this.myList = this.myList.map(item => Object.assign(
                {},
                item,
                {
                    like: item.like + (item.name === itemName ? 1 : 0)
                }
            ));
        }
    }
};
